        justify-content: space-around;
        .project-title {
          h3 {
            font-size: 3rem;
            font-family: $ff-raleway;
            font-weight: 300;
          }
          h4 {
            @include dflex();
            justify-content: space-between;
            padding: .8rem 0rem;
            span {
              font-size: 1rem;
              font-family: $ff-ptmono;
              font-weight: 200;
              line-height: 15px;
            }
          }
        }
        .external-links {
          @include dflex();
          a {
            img {
              width: 50%;
            }
          }
        }
      }
      .project-description {
        padding: 4rem 0rem;
        font-family: $ff-raleway;
        font-size: 2rem;
        font-weight: 200;
        opacity: 0.8;
        max-width: 500px;

      }
    }
  }
}

@media (min-width:780px) {
  .work-sec{
    .project-container{
      .project{
        height: 50vh;
        display: grid;
        grid-template-columns: 1fr 1fr;
        align-items: center;

        &:nth-child(odd){
     
            img{
              grid-column: 2/3;
            }
            .project-info{
              grid-column: 1/2;
              grid-row: 1/2;
            }

        }
        
        .project-info{
          .project-heading{

            .project-title{
              h4{
                span{
                  font-size: 1.3rem;
                  opacity: .8;
                }
              }
            }
            
          }
          .project-description{
            padding: 1rem 0rem;
            font-size: 1.5rem;
            width: 90%;
            margin: 0 auto;
            line-height: 1.5;
          }
        }
      }

    }
    
  }
}

